export const rightToc = [{
  value: 'Criar o Markdown',
  id: 'criar-o-markdown',
  children: []
}, {
  value: 'Definir estrutura sidebar (opcional)',
  id: 'definir-estrutura-sidebar-opcional',
  children: []
}, {
  value: 'Linkar a página no menu ou footer (opcional)',
  id: 'linkar-a-página-no-menu-ou-footer-opcional',
  children: [{
    value: 'Para adicionar um link à página no menu',
    id: 'para-adicionar-um-link-à-página-no-menu',
    children: []
  }, {
    value: 'Para adicionar um link à página no footer',
    id: 'para-adicionar-um-link-à-página-no-footer',
    children: []
  }]
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`Autor:`}</strong>{` `}<a parentName="p" {...{
          "href": "mailto:vitor.bellini@planejamento.gov.br"
        }}>{`Vitor Bellini`}</a>{`
em 21/08/2020`}</p>
    </blockquote>
    <p>{`O `}<a parentName="p" {...{
        "href": "https://v2.docusaurus.io/"
      }}>{`Docusaurus`}</a>{` é um código open-source criado pelo
Facebook para `}<strong parentName="p">{`simplificar`}</strong>{` a criação de páginas estáticas `}<strong parentName="p">{`bonitas`}</strong>{`.`}</p>
    <p>{`Para isso, transforma `}<a parentName="p" {...{
        "href": "https://www.markdownguide.org/"
      }}>{`markdown`}</a>{` em páginas html.`}</p>
    <p>{`A criação de uma nova página nesse site segue as seguintes etapas:`}</p>
    <h2 {...{
      "id": "criar-o-markdown"
    }}>{`Criar o Markdown`}</h2>
    <p>{`O arquivo markdown (.md) deve ser criado de baixo do diretório `}<inlineCode parentName="p">{`/docs`}</inlineCode>{` com a
seguinte estrutura no cabeçalho:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-markdown",
        "metastring": "{2,3}",
        "{2,3}": true
      }}>{`---
id: <<texto_sem_caracteres_especiais>>
title: <<Título da Página>>
---
`}</code></pre>
    <p>{`Parâmetros adicionais da página podem ser encontrados `}<a parentName="p" {...{
        "href": "https://v2.docusaurus.io/docs/next/markdown-features"
      }}>{`aqui`}</a>{`.`}</p>
    <p>{`E exemplos de sintaxe markdown `}<a parentName="p" {...{
        "href": "/examples/doc1"
      }}>{`aqui`}</a>{`.`}</p>
    <h2 {...{
      "id": "definir-estrutura-sidebar-opcional"
    }}>{`Definir estrutura sidebar (opcional)`}</h2>
    <p>{`O sidebar é a navegação das páginas naquele contexto. Se encontra no arquivo
`}<inlineCode parentName="p">{`/sidebar.js`}</inlineCode>{`. Para criação da estrutura de sidebar, adicione o seguinte código
no arquivo:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-js",
        "metastring": "{3,5,6,9,10,12,14,15}",
        "{3,5,6,9,10,12,14,15}": true
      }}>{`module.exports = {
  // ...
  qualquer_nome_do_contexto: [
    // para páginas que não estão em uma estrutura de hierarquia
    "diretorio/id_da_pagina_1",
    "diretorio/id_da_pagina_2",
    {
      // para páginas que estão em uma hierarquia
      type: "category",
      label: "Nome da Hierarquia",
      // se deseja que por default a hierarquia esteja aberta ou fechada
      collapsed: false,
      // links das páginas nas hierarquias
      items: ["diretorio/id_da_pagina_3",
              "diretorio/id_da_pagina_4"],
    },
  ],
`}</code></pre>
    <div {...{
      "className": "admonition admonition-note alert alert--secondary"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "14",
              "height": "16",
              "viewBox": "0 0 14 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M6.3 5.69a.942.942 0 0 1-.28-.7c0-.28.09-.52.28-.7.19-.18.42-.28.7-.28.28 0 .52.09.7.28.18.19.28.42.28.7 0 .28-.09.52-.28.7a1 1 0 0 1-.7.3c-.28 0-.52-.11-.7-.3zM8 7.99c-.02-.25-.11-.48-.31-.69-.2-.19-.42-.3-.69-.31H6c-.27.02-.48.13-.69.31-.2.2-.3.44-.31.69h1v3c.02.27.11.5.31.69.2.2.42.31.69.31h1c.27 0 .48-.11.69-.31.2-.19.3-.42.31-.69H8V7.98v.01zM7 2.3c-3.14 0-5.7 2.54-5.7 5.68 0 3.14 2.56 5.7 5.7 5.7s5.7-2.55 5.7-5.7c0-3.15-2.56-5.69-5.7-5.69v.01zM7 .98c3.86 0 7 3.14 7 7s-3.14 7-7 7-7-3.12-7-7 3.14-7 7-7z"
              }}></path></svg></span>{`note`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`O diretório da página não precisa inserir /docs.`}</p></div></div>
    <h2 {...{
      "id": "linkar-a-página-no-menu-ou-footer-opcional"
    }}>{`Linkar a página no menu ou footer (opcional)`}</h2>
    <p>{`A configuração do menu e footer são feitas no arquivo `}<inlineCode parentName="p">{`/docusaurus.config.js`}</inlineCode>{`.`}</p>
    <h3 {...{
      "id": "para-adicionar-um-link-à-página-no-menu"
    }}>{`Para adicionar um link à página no menu`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-js",
        "metastring": "{5-11}",
        "{5-11}": true
      }}>{`module.exports = {
    // ...
    navbar: {
      // ...
      items: [
        {
          to: '/diretorio/id_da_pagina',
          activeBasePath: 'docs/diretorio',
          label: 'Nome do Link',
          position: 'left',
        },
      ],
    },
`}</code></pre>
    <h3 {...{
      "id": "para-adicionar-um-link-à-página-no-footer"
    }}>{`Para adicionar um link à página no footer`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-js",
        "metastring": "{7-12}",
        "{7-12}": true
      }}>{`module.exports = {
    // ...
    footer: {
      // ...
      links: [
        {
          title: 'Nome do Título',
          items: [
            {
              label: 'Nome do Link',
              href: 'Link',
            },
            // novo link vem aqui na mesma estrutura
          ],
        },
      ],
    },
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
