const features = [
  {
    title: <>Automação extrema em projetos de fluxo e apresentação de dados</>,
    description: (
      <>
        Trabalhamos com <code>Airflow</code>, <code>Python</code> e várias outras
        tecnologias <b>open-source</b> para construir soluções de extração,
        transformação, carga e apresentação de dados.

        Nossa filosofia é automatizar ao máximo o trabalho repetitivo para que sempre possamos
        fazer trabalho novo.
      </>
    ),
  },
];

function OpenSource() {
  return (
    <section className={styles.features}>
      <div className="container">
        <div className="row">
          <div className={clsx('col col--8 col--offset-2', styles.opensource)}>
            <img src="img/open_source.png" className={styles.opensource}/>
          </div>
        </div>
    </div>
  </section>
  );
}

function Feature({title, description}) {
  return (
    <div className={clsx('col col--8 col--offset-2', styles.features2)}>
      <h3 className={styles.features2}>{title}</h3>
      <p>{description}</p>
    </div>
  );
}

function ProjectTitle() {
  return (
    <div className={styles.coverContainer}>
      <img src="img/team-cginf.png" className={styles.cover} />
      <h1 className={styles.cginf}>CGINF</h1>
      <h1 className={styles.time}>&#123; time de dados &#125;</h1>
    </div>
  );
}

function Home() {
  const context = useDocusaurusContext();
  const {siteConfig = {}} = context;
  return (
    <Layout
      title={`Hello from CGINF`}
      description="Site CGINF: Time de Dados <head />">
      <header className={styles.background}>
        <div className="container">
        <ProjectTitle />
          <div className={styles.buttons}>
            <Link
              className={clsx(
                'button button--outline button--secondary button--lg',
                styles.getStarted,
              )}
              to={useBaseUrl('monitoramento_rx/status_atualizacao')}>
              Monitoramento do Raio-X
            </Link>
          </div>
        </div>
      </header>
      <main>
        {features && features.length > 0 && (
          <section className={styles.features}>
            <div className="container">
              <div className="row">
                {features.map((props, idx) => (
                  <Feature key={idx} {...props} />
                ))}
              </div>
            </div>
          </section>
        )}
        <OpenSource />
      </main>
    </Layout>
  );
}

export default Home;
