  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'usando_locale',
	title: 'Usando o módulo locale do Python'
};
export const metadata = {
  "unversionedId": "share/usando_locale",
  "id": "share/usando_locale",
  "isDocsHomePage": false,
  "title": "Usando o módulo locale do Python",
  "description": "Autor: Augusto Herrmann",
  "source": "@site/docs/share/usando-locale.md",
  "permalink": "/share/usando_locale",
  "editUrl": "https://git.economia.gov.br/seges-cginf/cginf-site/edit/master/docs/share/usando-locale.md"
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Convertendo números',
  id: 'convertendo-números',
  children: [{
    value: 'Moedas',
    id: 'moedas',
    children: []
  }, {
    value: 'Meses e dias da semana',
    id: 'meses-e-dias-da-semana',
    children: []
  }]
}];

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <blockquote>
      <p parentName="blockquote"><strong parentName="p">{`Autor:`}</strong>{` `}<a parentName="p" {...{
          "href": "mailto:augusto.herrmann@planejamento.gov.br"
        }}>{`Augusto Herrmann`}</a>{`
em 20/08/2020`}</p>
    </blockquote>
    <p>{`Para converter dados à moda brasileira.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-python"
      }}>{`import locale
locale.setlocale(locale.LC_ALL, 'pt_BR.UTF-8')

# [output] 'pt_BR.UTF-8'
`}</code></pre>
    <h2 {...{
      "id": "convertendo-números"
    }}>{`Convertendo números`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-python"
      }}>{`locale.atof('1.000')

# [output] 1000.0
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-python"
      }}>{`locale.atof('0,5')

# [output] 0.5
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-python"
      }}>{`locale.atof('2.000,1')

# [output] 2000.1
`}</code></pre>
    <h3 {...{
      "id": "moedas"
    }}>{`Moedas`}</h3>
    <p>{`Também pode obter a string de moedas`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-python"
      }}>{`locale.currency(10.99)

# [output] 'R$ 10,99'
`}</code></pre>
    <h3 {...{
      "id": "meses-e-dias-da-semana"
    }}>{`Meses e dias da semana`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-python"
      }}>{`from datetime import datetime
`}</code></pre>
    <h4 {...{
      "id": "dia-da-semana"
    }}>{`Dia da semana`}</h4>
    <pre><code parentName="pre" {...{
        "className": "language-python"
      }}>{`datetime.now().strftime('%a')

# [output] 'qui'
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-python"
      }}>{`datetime.strptime('quarta', '%A')

# [output] datetime.datetime(1900, 1, 1, 0, 0)
`}</code></pre>
    <h4 {...{
      "id": "mês"
    }}>{`Mês`}</h4>
    <pre><code parentName="pre" {...{
        "className": "language-python"
      }}>{`datetime.now().strftime('%B')

# [output] 'agosto'
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-python"
      }}>{`datetime.strptime('ago/2019', '%b/%Y')

# [output] datetime.datetime(2019, 8, 1, 0, 0)
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
